import React,{Component} from 'react';
import CardList from './CardList';
import {Profile} from './Profile'
import SearchBox from './SearchBox';


class App extends Component{ 
    constructor(props){
        super();
        this.state={
            Profile:Profile,
            searchfield:""
        }
    }
    //user defined function to get the searched value
     onSearchChange=(event)=>{
        this.setState({
            searchfield:event.target.value
        })
    }

    render(){
        const FilteredArray=this.state.Profile.filter((arraydb)=>{
        return ((arraydb.name.toLowerCase()||arraydb.standard.toLowerCase()).includes(this.state.searchfield.toLowerCase()) );
        }
        );
      return(
            <div className="tc">
                <h1 className="">Shortlisted Profile's</h1>
                <SearchBox searchChange={this.onSearchChange}/>
                <CardList Profile={FilteredArray}/>
            </div>
        );
    }
}


export default App;
