import React,{Component} from 'react';
import CardList from './CardList';
import {Profile} from './Profile'
import SearchBox from './SearchBox';


class App extends Component{ 
    constructor(props){
        super();
        this.state={
            Profile:Profile,
            searchfield:""
        }
    }
    //user defined function to get the searched value
     onSearchChange=(event)=>{

        const FilteredArray=this.state.Profile.filter((arraydb)=>{
            //we dont write like this 
            // this.state.searchfield=event.target.value;
            //for updating
            this.setState({
                searchfield:event.target.value
            })
            return arraydb.name.toLowerCase().includes(this.state.searchfield.toLowerCase());
        }
        );
        console.log(FilteredArray);
    }

    render(){
      return(
            <div className="tc">
                <h1 className="">Shortlisted Profile's</h1>
                <SearchBox searchChange={this.onSearchChange}/>
                <CardList Profile={this.FilteredArray}/>
            </div>
        );
    }
}


export default App;
